// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Verifier.sol"; // Verifier szlemesini ie aktar

contract OrderManagement {
    Verifier public verifier; // Verifier szlemesine referans

    struct Order {
        uint orderId;
        string product;
        uint256 quantity;
        uint256 totalCost;
        string status;
        string customerName;
        address customerAddress;
        uint256[] inputValues;  // Zk-SNARK kantnda kullanlacak giri deerleri
        string ipfsHash; // IPFS hash'i ekle
    }

    mapping(uint256 => Order) public orders;
    uint256 public orderCount = 0;

    event OrderPlaced(uint256 orderId, string product, uint256 quantity, uint256 totalCost, string status, string customerName, string ipfsHash);
    event OrderVerified(uint256 orderId, bool verified);

    constructor(address verifierAddress) {
        verifier = Verifier(verifierAddress); // Verifier szlemesinin adresi ile balat
    }

    // Sipari oluturma fonksiyonu
    function placeOrder(
        string memory product,
        uint256 quantity,
        uint256 totalCost,
        string memory customerName,
        uint256[] memory inputValues, // zk-SNARK kantnda kullanlacak giri deerleri
        string memory ipfsHash // IPFS hash'i parametre olarak al
    ) public {
        orderCount++;
        orders[orderCount] = Order(orderCount, product, quantity, totalCost, "Pending", customerName, msg.sender, inputValues, ipfsHash);
        emit OrderPlaced(orderCount, product, quantity, totalCost, "Pending", customerName, ipfsHash);
    }

    // Sipari durumunu gncelleme fonksiyonu
    function updateOrderStatus(uint256 orderId, string memory status) public {
        Order storage order = orders[orderId];
        require(msg.sender == order.customerAddress, "Only customer can update order");
        order.status = status;
    }
function verifyOrder(uint256 orderId, Verifier.Proof memory proof) public returns (bool) {
    Order memory order = orders[orderId];

    // Ensure inputValues has the correct length
    require(order.inputValues.length == 4, "Input values should be of length 4");
    uint256[4] memory inputValuesArray;
    for (uint256 i = 0; i < 4; i++) {
        inputValuesArray[i] = order.inputValues[i];
    }

    // Verify the proof using the Verifier contract
    bool isVerified = verifier.verifyTx(proof, inputValuesArray);

    // Emit the verification result
    emit OrderVerified(orderId, isVerified);

    // Update the order status if verified
    if (isVerified) {
        orders[orderId].status = "Verified"; // Sipariin durumunu "Verified" olarak gncelle
    }

    return isVerified; // Return the verification result
}

    

    // Siparii alma fonksiyonu
    function getOrder(uint256 orderId) public view returns (Order memory) {
        return orders[orderId];
    }
}
